  "data": {
    "potential_symptoms": [
      {
        "symptom_id": "symp_001",
        "name_localized": "Lesões cutâneas escamosas e inflamadas",
        "suggestion_localized": "Observe a presença de manchas vermelhas com escamas prateadas na pele.",
        "explanation_localized": "Este é um sintoma comum da psoríase, muitas vezes agravado por hábitos alimentares inadequados e padrões de sono irregulares em crianças."
      },
      {
        "symptom_id": "symp_002",
        "name_localized": "Coceira intensa na pele",
        "suggestion_localized": "Procure por áreas com coceira que podem piorar à noite ou após certas refeições.",
        "explanation_localized": "A coceira frequente pode estar relacionada ao impacto das causas selecionadas na saúde da pele, especialmente em relação à alimentação e sono."
      },
      {
        "symptom_id": "symp_003",
        "name_localized": "Cortes ou rachaduras na pele",
        "suggestion_localized": "Fique atento a áreas que possam rachar ou sangrar facilmente, especialmente em áreas afetadas pela psoríase.",
        "explanation_localized": "A secura e o risco de rachaduras podem ser exacerbados pelo padrão de sono e hábitos nutricionais desfavoráveis."
      },
      {
        "symptom_id": "symp_004",
        "name_localized": "Irritabilidade e fadiga",
        "suggestion_localized": "Observe alterações no humor e níveis de energia, especialmente após noites de sono irregular.",
        "explanation_localized": "Distúrbios do sono e má alimentação podem contribuir para alterações emocionais e cansaço em crianças com psoríase."
      },
      {
        "symptom_id": "symp_005",
        "name_localized": "Sensação de ardor ou queimação na pele",
        "suggestion_localized": "Note qualquer sensação de queimação ou desconforto nas áreas afetadas.",
        "explanation_localized": "Este sintoma pode estar relacionado ao processo inflamatório da psoríase, agravado por fatores como alimentação inadequada."
      },
      {
        "symptom_id": "symp_006",
        "name_localized": "Manchas descoloridas na pele após lesões",
        "suggestion_localized": "Observe quaisquer mudanças na coloração da pele ao redor das lesões psoriásicas.",
        "explanation_localized": "Alterações na pigmentação podem ocorrer devido a fatores de saúde relacionados à nutrição e padrão de sono."
      },
      {
        "symptom_id": "symp_007",
        "name_localized": "Alterações no humor ou ansiedade",
        "suggestion_localized": "Fique atento a sinais de ansiedade ou alterações emocionais associadas ao desconforto da psoríase.",
        "explanation_localized": "O desconforto físico e fatores de estilo de vida podem contribuir para sintomas emocionais em crianças."
      }
    ]
  },
